import React, {useState,useEffect} from 'react'
import CardComponent from './card_component';
import "../styles/listing.css"
import {v4 as uuid4} from "uuid";
import DetailComponent from './detail_component';

const ListProperties = ({properties, search}) => {
    console.log("***")
    console.log(properties)
    const rows = [];
    const [isClick, setClick] = useState(true);

    const submitHandler = (isClick, setClick) => {
        console.log("***")
        if (isClick) {
            setClick(false);
        } else {
            setClick(true);
        }
      }



    properties.forEach((property) =>  {
        if (search) {
            if ( 
                search.location &&
                (property.city.toLowerCase().indexOf(search.location.toLowerCase()) === -1) &&
                (property.state.toLowerCase().indexOf(search.location.toLowerCase()) === -1)
            ) {
                return;
            }
            if (
                search.type &&
                (property.title.toLowerCase().indexOf(search.type.toLowerCase()) === -1)
            ) {
                return;
            }
        }
        rows.push(property);
    });


    return (
        <div className="row" id="listing">
            <div className="row row-cols-2 row-cols-md-3 row-cols-lg-4 g-4">
                {
                    rows.map(row => {
                        return (isClick? <CardComponent key={row.id} properties={row} isClick={isClick} setClick={setClick} submitHandler={submitHandler}/> :<DetailComponent/>)
                    })
                }
            </div>
        </div>
    )
}

const ListComponent = ({search, setSearch}) => {
    // console.log(search);
    const [data,setData]=useState([]);
    const getData=()=>{
        fetch('properties.json'
        ,{
            headers : { 
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            }
        }
        )
        .then(function(response){
            console.log(response)
            return response.json();
        })
        .then(function(myJson) {
            console.log(myJson);
            setData(myJson)
        });
    }
    useEffect(()=>{
        getData()
    },[])

  return (
    <div>
        <div className="container" id="housing">
            <ListProperties properties={data} search={search}/>
        </div>
    </div>
  )
}

export default ListComponent
