import {React, useState, useEffect} from 'react';
import HomeP1Component from './home_p1_component';
import HomeP2Component from './home_p2_component';
import DetailComponent from './detail_component';
import CardComponent from './card_component';
import "../styles/home_p1.css";


const ListProperties = ({properties, search}) => {
  const rows = [];
  const [isClick, setClick] = useState({click: false, id:"-1"});
  console.log(isClick)

  properties.forEach((property) =>  {
    if (rows.length > 3) {
      return
    }
      rows.push(property);
  });


  return (
      <div className="row" id="listing">
          <div className="row row-cols-2 row-cols-md-3 row-cols-lg-4 g-4">
              {
                  rows.map(row => {
                      return (isClick.click && isClick.id===row.id ? <DetailComponent/> :<CardComponent key={row.id} properties={row} isClick={isClick} setClick={setClick}/>)
                  })
              }
          </div>
      </div>
  )
}

const HomeComponent = () => {
  const [data,setData]=useState([]);
  const getData=()=>{
      fetch('properties.json'
      ,{
          headers : { 
              'Content-Type': 'application/json',
              'Accept': 'application/json'
          }
      }
      )
      .then(function(response){
          console.log(response)
          return response.json();
      })
      .then(function(myJson) {
          console.log(myJson);
          setData(myJson)
      });
  }
  useEffect(()=>{
      getData()
  },[])

  return (
    <div>
      <div className="container-fluid fill">
        <HomeP1Component />
        <ListProperties properties={data}/>
      </div>
      <div className="container-fluid fill" style={{backgroundColor: "yellow"}}>
        <HomeP2Component />
      </div>
    </div>
  )
}

export default HomeComponent
